import React, { memo } from 'react';
import { msToTime } from 'utils/utils';
import styled from 'styled-components';
import Theme from 'app/assets/theme/';
import CircularIncrement from 'app/views/components/Test/ResultCircle';
import TestListItem from 'app/views/components/Test/TestListItem';
import { Person } from '@material-ui/icons';
import { Grid } from '@material-ui/core';
import LoadingIndicator from 'app/views/components/common/LoadingIndicator';

const LoadingWrapper = styled(Grid)`
	justify-content: center;
	align-items: center;
`;

const NoTestFoundWrapper = styled.div`
	display: flex;
	justify-content: center;
	align-items: center;
	height: 100vh;
`;

const UserInfo = styled.div`
	width: 100%;
	display: flex;
	justify-content: center;
	align-items: center;
	flex-direction: column;
`;

const UserAvatar = styled.img`
	width: 65px;
	height: 65px;
	border-radius: 50%;
`;

const AvatarWrapper = styled.div`
	width: 65px;
	height: 65px;
	display: flex;
	border-radius: 50%;
	justify-content: center;
	align-items: center;
	border: 1px solid ${Theme.ncpPrimary};
`;

const UserName = styled.p`
	margin: 8px;
	font-weight: 700;
	color: ${Theme.ncpDark};
	width: 100%;
	font-size: 16px;
	text-align: center;
	line-height: 1.5;
`;

const ScoreSection = styled(Grid)`
	margin-top: 50px;
	@media only screen and (max-width: 480px) {
		margin: 30px 0px;
	}
`;

const ScoreTitle = styled.h2`
	width: 100%;
	text-align: center;
	color: ${Theme.ncpPrimaryDark};
	font-weight: 700;
`;

const ScoreDetails = styled.p`
	display: block;
	margin-bottom: 0px;
	font-size: ${props => (props.fontsize ? props.fontsize : '16')}px;
	font-weight: 700;
	text-align: center;

	${props =>
		props.iscolored === 'true'
			? `
  color: ${Theme.ncpPrimaryDark};`
			: `
  color: ${Theme.ncpBlack}`}
`;

const ScoreDetailsBlock = styled.div`
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	width: 30%;
`;

const ResultSection = styled.div`
	width: 100%;
	display: flex;
	align-items: center;
	justify-content: space-around;
	margin-top: 20px;

	@media only screen and (max-width: 525px) {
		margin-top: 0px;
		flex-direction: column;
		justify-content: center;
	}
`;

const ResultChart = styled.div`
	@media only screen and (max-width: 525px) {
		margin-top: 30px;
	}
`;

const TestInfoBlock = styled.div`
	margin-top: -45px;
`;

const ResultDetails = props => {
	const { history, resultRes } = props;
	if (resultRes.loading) {
		return (
			<LoadingWrapper container>
				<LoadingIndicator></LoadingIndicator>
			</LoadingWrapper>
		);
	}

	if (!resultRes.data || resultRes.error) {
		return <NoTestFoundWrapper>Something went wrong, please try again later</NoTestFoundWrapper>;
	}

	const { test, result, user } = resultRes.data.getReadingResult;
	return (
		<React.Fragment>
			<TestInfoBlock>
				<TestListItem history={history} test={test}></TestListItem>
			</TestInfoBlock>

			<ScoreSection container>
				<UserInfo>
					<AvatarWrapper>
						{user.avatar ? (
							<UserAvatar src={user.avatar}></UserAvatar>
						) : (
							<Person fontSize="large" color="primary"></Person>
						)}
					</AvatarWrapper>
					<UserName>{user.username ? user.username : user.firstName + ' ' + user.lastName}</UserName>
				</UserInfo>
				<ScoreTitle>The score is:</ScoreTitle>
				<ResultSection>
					<ResultChart>
						<CircularIncrement thickness={2} value={(result.correctAnswers * 100) / 40} size="120px">
							<ScoreDetailsBlock>
								<ScoreDetails>Correct Answers</ScoreDetails>
								<ScoreDetails iscolored="true">
									{result.correctAnswers}/{result.totalQuestions}
								</ScoreDetails>
							</ScoreDetailsBlock>
						</CircularIncrement>
					</ResultChart>
					<ResultChart>
						<CircularIncrement thickness={3.6} value={(result.scoreBand * 100) / 9} size="140px">
							<ScoreDetailsBlock>
								<ScoreDetails iscolored="true" fontsize={40}>
									{result.scoreBand}
								</ScoreDetails>
							</ScoreDetailsBlock>
						</CircularIncrement>
					</ResultChart>
					<ResultChart>
						<CircularIncrement thickness={2} value={(result.timeSpent * 100) / 3600000} size="120px">
							<ScoreDetailsBlock>
								<ScoreDetails>Time spent</ScoreDetails>
								<ScoreDetails iscolored="true">{msToTime(result.timeSpent)}</ScoreDetails>
							</ScoreDetailsBlock>
						</CircularIncrement>
					</ResultChart>
				</ResultSection>
			</ScoreSection>
		</React.Fragment>
	);
};

export default memo(ResultDetails);
