import React, { useState } from 'react';
import styled from 'styled-components';
import { TextField, IconButton } from '@material-ui/core';
import { Cancel, Done, Delete } from '@material-ui/icons';
import Theme from 'app/assets/theme';
import { EscapeQuotationMarks } from 'utils/utils';

const ActionButtons = styled.div`
	position: absolute;
	display: flex;
	flex-direction: row !important;
	justify-content: space-between;
	align-items: center;
	bottom: -45px;
	right: 2px;
`;

const Wrapper = styled.div`
	position: relative;
	width: 100%;
`;

function ParagraphEditor(props) {
	const {
		paragraphContent = '',
		handleChangeEditState,
		idx,
		isQuestionEdit = false,
		handleConfirm,
		labelStyle = { fontSize: '16px', fontWeight: '300', lineHeight: '1.5' },
	} = props;
	const [content, changeContent] = useState(paragraphContent);

	const handleOnChange = event => {
		changeContent(event.target.value);
	};

	const handleOnCancel = () => {
		changeContent(paragraphContent);
		handleChangeEditState(false);
	};

	const handleOnConfirm = () => {
		const refinedContent = EscapeQuotationMarks(content);
		handleConfirm(refinedContent, idx);
		handleChangeEditState(false);
	};

	return (
		<Wrapper idx={idx} isquestionedit={isQuestionEdit.toString()}>
			<TextField
				value={content}
				id="outlined-textarea"
				label="Insert paragraph"
				placeholder="Insert paragraph"
				multiline
				fullWidth
				variant="outlined"
				onChange={event => handleOnChange(event)}
				InputProps={{
					style: labelStyle,
				}}
			/>

			<ActionButtons>
				<IconButton size="medium" onClick={() => handleOnCancel()}>
					<Cancel color="error"></Cancel>
				</IconButton>

				<IconButton size="medium" onClick={() => handleOnConfirm()}>
					<Done color="primary"></Done>
				</IconButton>
			</ActionButtons>
		</Wrapper>
	);
}

export default ParagraphEditor;
