import React, { useState } from 'react';
import { Grid, TextField } from '@material-ui/core';
import { KeyboardDatePicker } from '@material-ui/pickers';
import PassageImageEditor from '../AdminCreateReadingTest/components/PassageImageEdit';

const labelStyle = { fontSize: '16px', fontWeight: '300', lineHeight: '1.5' };

function TestEditor(props) {
	const { test, modifyTestData } = props;

	const handleChangeTestTitle = event => {
		const newTestData = { ...test, title: event.target.value };
		modifyTestData(newTestData);
	};

	const handleChangeDate = date => {
		const selectedDate = date._d;
		const newTestData = { ...test, publishDate: selectedDate };
		modifyTestData(newTestData);
	};

	const handleChangeImage = image => {
		const newTestData = { ...test, image: image };
		modifyTestData(newTestData);
	};

	const { title, image } = test;
	return (
		<div>
			<Grid container spacing={4}>
				<Grid item xs={4}>
					<PassageImageEditor src={image} handleAddImage={handleChangeImage}></PassageImageEditor>
				</Grid>
				<Grid item xs={8}>
					<Grid item xs={12}>
						<TextField
							value={title}
							id="outlined-textarea"
							label="Test Title"
							placeholder="Test Title"
							multiline
							fullWidth
							variant="outlined"
							onChange={event => handleChangeTestTitle(event)}
							InputProps={{
								style: labelStyle,
							}}
						/>
					</Grid>
					<Grid item xs={12} style={{ marginTop: '35px' }}>
						<KeyboardDatePicker
							autoOk
							fullWidth
							variant="inline"
							inputVariant="outlined"
							label="Publish date"
							format="DD/MM/YYYY"
							value={test.publishDate}
							InputAdornmentProps={{ position: 'start' }}
							onChange={date => handleChangeDate(date)}
						/>
					</Grid>
				</Grid>
			</Grid>
		</div>
	);
}

export default TestEditor;
