import React, { memo } from 'react';
import styled from 'styled-components';
import { Grid, Typography } from '@material-ui/core';
import { useQuery } from '@apollo/client';
import TestQueries from 'graphql/queries/Test';
import LoadingIndicator from 'app/views/components/common/LoadingIndicator';
import FeaturedTestsSkeleton from '../../../components/Skeletons/FeaturedTestsSkeleton';
import TestCardItemSkeleton from 'app/views/components/Skeletons/TestCardItemSkeleton';

const TestCardItem = React.lazy(() => import('app/views/components/Test/TestCardItem'));

const Wrapper = styled(Grid)`
	width: 100%;
	background: white;
	align-items: center;
	${props =>
		props.lowpadding === 'true'
			? `padding: 40px 50px;`
			: `padding: 40px 250px;
	`}
	@media screen and (max-width: 1024px) {
		padding: 40px 30px;
	}

	@media screen and (min-width: 1024px) and (max-width: 1300px) {
		padding: 40px 30px;
	}
`;

const BlockTitle = styled(Typography)`
	margin-bottom: 20px !important;
	font-weight: 600 !important;
`;

const TestListContainer = styled(Grid)`
	align-items: center;
`;

const ErrorText = styled(Typography)`
	text-align: center;
`;

function FeaturedTests(props) {
	const { title = '', lowPadding = false, wrapperStyle } = props;
	const getTestRes = useQuery(TestQueries.GET_TESTS, { variables: { offset: 0, limit: 4 } });
	const { data, error, loading } = getTestRes;

	if (error) {
		return (
			<Wrapper style={wrapperStyle} lowpadding={lowPadding.toString()}>
				<BlockTitle variant="h5">Featured Tests</BlockTitle>
				{loading && <FeaturedTestsSkeleton></FeaturedTestsSkeleton>}
				{!data && !loading && (
					<ErrorText color="error" variant="h6" component="p">
						No photos found
					</ErrorText>
				)}
				{error && (
					<ErrorText color="error" variant="h6" component="p">
						Something went wrong, please try again later
					</ErrorText>
				)}
			</Wrapper>
		);
	}

	return (
		<Wrapper style={wrapperStyle} lowpadding={lowPadding.toString()}>
			<BlockTitle variant="h5">{title}</BlockTitle>
			<TestListContainer container spacing={6}>
				{loading && <FeaturedTestsSkeleton></FeaturedTestsSkeleton>}
				{data &&
					data.tests.map((test, idx) => (
						<Grid item xs={12} sm={6} md={3} key={idx}>
							<React.Suspense fallback={<TestCardItemSkeleton />}>
								<TestCardItem testId={test.id} title={test.title} rating={test.rating}></TestCardItem>
							</React.Suspense>
						</Grid>
					))}
			</TestListContainer>
		</Wrapper>
	);
}

export default memo(FeaturedTests);
