import { Button, FormControlLabel, Grid, Switch, TextField, Tooltip, Typography } from '@material-ui/core';
import MarkdownEditor from 'app/views/components/MarkdownEditor';
import React, { useState } from 'react';
import BlogMutations from 'graphql/mutations/Blog';
import { useMutation } from '@apollo/client';
import { getMessage } from 'utils/messageConvert';
import { FormattedMessage, injectIntl } from 'react-intl';
import LoadingIndicator from 'app/views/components/common/LoadingIndicator';
import { withSnackbar } from 'notistack';
import { useHistory } from 'react-router-dom';
import PassageImageEditor from '../AdminCreateTest/components/AdminCreateReadingTest/components/PassageImageEdit';

function AdminCreateBlog(props) {
	const [title, setTitle] = useState('');
	const [isPublished, setIsPublished] = useState(false);
	const [image, setImage] = useState('');
	const [content, setContent] = useState({ html: '', text: '' });
	const { intl } = props;
	const history = useHistory();
	// CREATE
	const [createBlog, createBlogRes] = useMutation(BlogMutations.CREATE_BLOG, {
		onCompleted: () => handleCreateSuccess(),
		onError: err => handleCreateFailure(err),
	});

	const handleCreateSuccess = () => {
		const messageId = 'MANAGE.BLOGS.CREATE.SUCCESS';
		props.enqueueSnackbar(
			intl.formatMessage({
				id: messageId,
			}),
			{
				variant: 'success',
				anchorOrigin: {
					vertical: 'top',
					horizontal: 'right',
				},
			},
		);
		history.push('/admin/blogs');
	};

	const handleCreateFailure = err => {
		if (!err) {
			return;
		}
		let message = getMessage(err.message);

		props.enqueueSnackbar(
			intl.formatMessage({
				id: message,
			}),
			{
				variant: 'error',
				anchorOrigin: {
					vertical: 'top',
					horizontal: 'right',
				},
			},
		);
	};
	const handleChangeContent = (html, text) => {
		setContent({ html, text });
	};

	const handleChangeTitle = e => {
		setTitle(e.target.value);
	};

	const handleChangeIsPublished = event => {
		setIsPublished(event.target.checked);
	};

	const handleSubmit = () => {
		const post = {
			title: title,
			image: image,
			isPublished: isPublished,
			content: content,
		};
		createBlog({ variables: { post: post } });
	};

	const handleAddImage = base64String => {
		setImage(base64String);
	};

	return (
		<div className="kt-portlet kt-portlet--height-fluid">
			<div className="kt-portlet__head">
				<div className="kt-portlet__head-label">
					<Typography variant="h5" className="kt-portlet__head-title">
						Blog Editor
					</Typography>
				</div>
			</div>
			<div className="kt-portlet__body">
				<Grid container spacing={3}>
					<Grid item xs={12} md={6}>
						<TextField
							id="outlined-basic"
							label="Title"
							value={title}
							variant="outlined"
							fullWidth
							onChange={handleChangeTitle}
						/>

						<FormControlLabel
							className="mt-3"
							control={
								<Tooltip
									title="If toggled on, the post will be displayed publicly."
									aria-label="Is published"
								>
									<Switch
										checked={isPublished}
										onChange={handleChangeIsPublished}
										name="Is Published"
										color="primary"
									/>
								</Tooltip>
							}
							label="Is Published"
						/>
					</Grid>
					<Grid item xs={12} md={6}>
						<PassageImageEditor handleAddImage={handleAddImage} src={image}></PassageImageEditor>
					</Grid>
				</Grid>
				<div className="mt-5">
					<MarkdownEditor handleChange={handleChangeContent} />
				</div>

				<div className="ml-auto mt-5">
					<Button disabled={createBlogRes.loading} onClick={handleSubmit} variant="contained" color="primary">
						Submit
						{createBlogRes.loading && <LoadingIndicator width="10px" height="10px"></LoadingIndicator>}
					</Button>
				</div>
			</div>
		</div>
	);
}

export default injectIntl(withSnackbar(AdminCreateBlog));
