import React from 'react';
import { FormattedMessage, injectIntl } from 'react-intl';

class MenuItemText extends React.Component {
	render() {
		const { intl } = this.props;
		return (
			<>
				{this.props.item.icon && <i className={`kt-menu__link-icon ${this.props.item.icon}`} />}

				{this.props.parentItem && this.props.parentItem.bullet === 'dot' && (
					<i className="kt-menu__link-bullet kt-menu__link-bullet--dot">
						<span />
					</i>
				)}

				{this.props.parentItem && this.props.parentItem.bullet === 'line' && (
					<i className="kt-menu__link-bullet kt-menu__link-bullet--line">
						<span />
					</i>
				)}

				<span className="kt-menu__link-text" style={{ whiteSpace: 'nowrap' }}>
					{this.props.item.translate
						? intl.formatMessage({
								id: this.props.item.translate,
						  })
						: this.props.item.title}
				</span>

				{this.props.item.badge && (
					<span className="kt-menu__link-badge">
						<span className={`kt-badge ${this.props.item.badge.type}`}>{this.props.item.badge.value}</span>
					</span>
				)}

				{this.props.item.submenu && <i className="kt-menu__ver-arrow la la-angle-right" />}
			</>
		);
	}
}

export default injectIntl(MenuItemText);
