import React from 'react';
import { Grid } from '@material-ui/core';
import SingleFeature from 'app/views/components/Landing/SingleFeature';
import { DirectionsOutlined, ForumOutlined, DevicesOutlined } from '@material-ui/icons';

const FeatureMockData = [
	{
		title: 'Directions-oriented',
		description:
			'We analyze your test data, and achivements, as well as your strategy in timing, noting, to provide you directions for your improvement.',
		icon: DirectionsOutlined,
	},
	{
		title: 'Community focused',
		description:
			'You can chat, comment, sharing your exam scores and achivements, as well as blogging, so that knowledge will be passed on and shared.',
		icon: ForumOutlined,
	},
	{
		title: 'Multiple platforms',
		description:
			'Practice your skills on any platforms from phone, tablet to desktop, as long as it is connected to the internet. Thus, remove all the troubles of printing, keeping track on documents, and audio files.',
		icon: DevicesOutlined,
	},
];

function Features(props) {
	return (
		<Grid container spacing={4} direction="row" justify="space-around" alignItems="center">
			{FeatureMockData.map((feature, idx) => (
				<Grid item xs={12} md={3} key={idx}>
					<SingleFeature
						title={feature.title}
						description={feature.description}
						Icon={feature.icon}
					></SingleFeature>
				</Grid>
			))}
		</Grid>
	);
}

export default Features;
