/* eslint-disable no-script-url,jsx-a11y/anchor-is-valid */
import React from 'react';
import { Link, Redirect } from 'react-router-dom';
import Dropdown from 'react-bootstrap/Dropdown';
import { connect } from 'react-redux';
import { toAbsoluteUrl } from '../../../../themes/_metronic';
import HeaderDropdownToggle from '../CustomDropdowns/HeaderDropdownToggle';
import { useLazyQuery } from '@apollo/client';
import UserQueries from 'graphql/queries/User';
import { CircularProgress, Button, Dialog, Avatar } from '@material-ui/core';
import history from 'utils/history';
import authActions from 'state/ducks/auth/actions';
import LoadingIndicator from '../../common/LoadingIndicator';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

const AuthPage = React.lazy(() => import('app/views/pages/AuthPage/Injectable'));

function UserProfile(props) {
	const { showHi, showAvatar, showBadge, activeUser, logout } = props;
	const [loginModalState, setOpenLoginModal] = React.useState({ isOpen: false, formType: 'login' });
	const [getProfileId, profileIdRes] = useLazyQuery(UserQueries.GET_MY_PROFILE, {
		onCompleted: () => history.push(`/user/${profileIdRes.data.getMyProfile.userId}`),
		fetchPolicy: 'network-only',
	});

	const handleOpenLoginModal = (isOpen, formType) => {
		return setOpenLoginModal({ isOpen: isOpen, formType: formType });
	};

	const [profileMenuOpen, setProfileMenuOpen] = React.useState(null);

	const handleOpenProfileMenu = (event) => {
		setProfileMenuOpen(event.currentTarget);
	};

	const handleCloseProfileMenu = () => {
		setProfileMenuOpen(null);
	};
	const user = activeUser;

	if (user.username) {
		const welcomeName =
			user.firstName && user.lastName
				? user.firstName + ' ' + user.lastName
				: user.username
				? user.username
				: 'Undefined';
		return (
			<Dropdown className="kt-header__topbar-item kt-header__topbar-item--user" drop="down" alignRight>
				<Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleOpenProfileMenu}>
					{showHi && <span className="kt-header__topbar-welcome kt-hidden-mobile">Hi,</span>}

					{showAvatar && (

!user.avatar ? (
	<Avatar>
		{welcomeName.charAt(0)}
	</Avatar>
) : <Avatar alt="" src={user.avatar}></Avatar>
)}
					<span className="kt-header__topbar-username kt-hidden-mobile">{welcomeName}</span>}

				</Button>
				<Menu
        id="simple-menu"
        anchorEl={profileMenuOpen}
        keepMounted
        open={Boolean(profileMenuOpen)}
        onClose={handleCloseProfileMenu}
      >
        <MenuItem onClick={handleCloseProfileMenu}>Profile</MenuItem>
        <MenuItem onClick={handleCloseProfileMenu}>My account</MenuItem>
        <MenuItem onClick={handleCloseProfileMenu}>Logout</MenuItem>
      </Menu>
				<Dropdown.Menu
					style={{ zIndex: 90 }}
					className="dropdown-menu dropdown-menu-fit dropdown-menu-right dropdown-menu-anim dropdown-menu-top-unround dropdown-menu-xl"
				>
					{/** ClassName should be 'dropdown-menu dropdown-menu-fit dropdown-menu-right dropdown-menu-anim dropdown-menu-top-unround dropdown-menu-xl' */}
					<div
						className="kt-user-card kt-user-card--skin-dark kt-notification-item-padding-x"
						style={{
							backgroundImage: `url()`,
						}}
					>
						<div className="kt-user-card__avatar">
							{!user.avatar ? (
								<span className="kt-badge kt-badge--lg kt-badge--rounded kt-badge--bold  kt-font-success">
									{welcomeName.charAt(0)}
								</span>
							) : (
								<span
									className="kt-badge kt-badge--lg kt-badge--rounded kt-badge--bold  kt-font-success"
									style={{
										borderRadius: '50%',
										backgroundSize: 'cover',
										backgroundImage: `url(${user.avatar})`,
									}}
								></span>
							)}
						</div>
						<div className="kt-user-card__name">{welcomeName}</div>
						{/* <div className="kt-user-card__badge">
								<span className="btn btn-success btn-sm btn-bold btn-font-md">23 messages</span>
							</div> */}
					</div>
					<div className="kt-notification">
						<a className="kt-notification__item" onClick={() => getProfileId()}>
							<div className="kt-notification__item-icon">
								<i className="flaticon2-calendar-3 kt-font-success" />
							</div>
							<div className="kt-notification__item-details">
								<div className="kt-notification__item-title kt-font-bold">My Profile</div>
								<div className="kt-notification__item-time">Account settings and more</div>
							</div>
							{profileIdRes.loading && <CircularProgress color="primary" size="40px"></CircularProgress>}
						</a>
						{/* <a className="kt-notification__item">
								<div className="kt-notification__item-icon">
									<i className="flaticon2-mail kt-font-warning" />
								</div>
								<div className="kt-notification__item-details">
									<div className="kt-notification__item-title kt-font-bold">My Messages</div>
									<div className="kt-notification__item-time">Inbox and tasks</div>
								</div>
							</a> */}
						{/* <a className="kt-notification__item">
							<div className="kt-notification__item-icon">
								<i className="flaticon2-rocket-1 kt-font-danger" />
							</div>
							<div className="kt-notification__item-details">
								<div className="kt-notification__item-title kt-font-bold">My Activities</div>
								<div className="kt-notification__item-time">Logs and notifications</div>
							</div>
						</a> */}
						{/* <a className="kt-notification__item">
								<div className="kt-notification__item-icon">
									<i className="flaticon2-hourglass kt-font-brand" />
								</div>
								<div className="kt-notification__item-details">
									<div className="kt-notification__item-title kt-font-bold">My Tasks</div>
									<div className="kt-notification__item-time">latest tasks and projects</div>
								</div>
							</a> */}
						<div className="kt-notification__custom">
							<Button onClick={() => logout()} className="btn btn-label-brand btn-sm btn-bold">
								Sign Out
							</Button>
						</div>
					</div>
				</Dropdown.Menu>
			</Dropdown>
		);
	}

	return (
		<React.Fragment>
			<React.Fragment>
				<Dialog
					onClose={() => handleOpenLoginModal(false)}
					aria-labelledby="login-modal-in-authorize-popup"
					open={loginModalState.isOpen}
				>
					<React.Suspense fallback={<LoadingIndicator hasSpacing />}>
						<AuthPage isModal={true} formTypeDefault={loginModalState.formType}></AuthPage>
					</React.Suspense>
				</Dialog>
			</React.Fragment>
			<Button color="primary" size="large" onClick={() => handleOpenLoginModal(true, 'login')}>
				Login
			</Button>
			<Button color="primary" size="large" onClick={() => handleOpenLoginModal(true, 'register')}>
				Sign up
			</Button>
		</React.Fragment>
	);
}

const mapStateToProps = ({ auth: { activeUser } }) => ({
	activeUser,
});

const mapDispatchToProps = dispatch => {
	const logout = () => dispatch(authActions.logout());

	return {
		logout,
	};
};

export default connect(mapStateToProps, mapDispatchToProps)(UserProfile);
