import React from 'react';
import { Grid, Typography, Button } from '@material-ui/core';
import styled from 'styled-components';
import { useHistory } from 'react-router';

const Wrapper = styled.div`
	width: 100%;
	background: url('https://res.cloudinary.com/doyyjeich/image/upload/v1598254358/onlineExam/assets/backgrounds/hero/landing-hero_jjmsdc.jpg');
	background-size: 520px;
	background-color: white;
	background-repeat: no-repeat;
	background-position: right 25% bottom 45%;

	@media screen and (min-width: 960px) and (max-width: 1300px) {
		background-position: right 5% bottom 45%;
	}

	@media screen and (max-width: 959px) {
		margin-top: 60px;
		background: white;
	}
`;

const HeroContent = styled(Grid)`
	display: flex;
	align-items: center;
	padding: 100px 250px;
	@media screen and (max-width: 1024px) {
		padding: 100px 30px;
	}

	@media screen and (min-width: 1024px) and (max-width: 1300px) {
		padding: 100px 30px;
	}
`;

const ActionWrapper = styled.div`
	margin-top: 30px;
	display: flex;
	align-items: center;
`;

const SecondaryButton = styled(Button)`
	margin-left: 30px !important;
`;

const AdditionalInfo = styled(Typography)`
	margin-top: 30px !important;
`;

function Hero(props) {
	const history = useHistory();
	const primaryAction = () => {
		history.push('/collection');
	};

	return (
		<Wrapper>
			<HeroContent container>
				<Grid item xs={12} md={7}>
					<Typography variant="h3" component="h1">
						Ielts Test Platform Showcase
					</Typography>
					<AdditionalInfo variant="h6" component="h2" color="textSecondary">
						Here we offer plenty of tests and online test taking platform for you to practice, with multiple
						tools and other fun stuffs such as achivements, records and analytics to help you improve and
						stay motivated.
					</AdditionalInfo>
					<ActionWrapper>
						<Button variant="contained" size="large" color="primary" onClick={() => primaryAction()}>
							Try now for free
						</Button>
						<SecondaryButton variant="outlined" size="large" color="secondary">
							Learn more
						</SecondaryButton>
					</ActionWrapper>
				</Grid>
			</HeroContent>
		</Wrapper>
	);
}

export default Hero;
