import React, { useState } from 'react';
import { Link, Redirect, Route, Switch } from 'react-router-dom';
import LoginForm from 'app/views/components/AuthForms/LoginForm';
import RegistrationForm from 'app/views/components/AuthForms/RegistrationForm';
import ForgotPasswordForm from 'app/views/components/AuthForms/ForgotPasswordForm';
import { toAbsoluteUrl } from 'app/themes/_metronic';
import 'app/themes/_metronic/_assets/sass/pages/login/login-1.scss';
import logo from 'app/assets/media/logos/300x300white.png';
import ResetPasswordForm from 'app/views/components/AuthForms/ResetPasswordForm';
import VerifyEmailForm from 'app/views/components/AuthForms/VerifyEmailForm';
import { useLazyQuery, useMutation } from '@apollo/client';
import UserMutations from 'graphql/mutations/User';
import AuthQueries from 'graphql/queries/Auth';

export default function AuthPage(props) {
	const { isModal = false, dispatch, formTypeDefault = 'login' } = props;
	const [formType, changeFormType] = useState(formTypeDefault);
	const [login, loginRes] = useLazyQuery(AuthQueries.LOG_IN);
	const [register, registerRes] = useMutation(UserMutations.CREATE_USER);

	const handleLogin = (email, password) => {
		return login({
			variables: { email: email, password: password },
		});
	};

	const handleRegister = (email, password, username, firstName, lastName, phone) => {
		const parseStringPhone = phone.toString();
		return register({
			variables: {
				user: {
					email,
					password,
					username,
					firstName,
					lastName,
					phone: parseStringPhone,
				},
			},
		});
	};

	const handleChangeFormType = (value = 'login') => {
		changeFormType(value);
	};

	if (!isModal) {
		return (
			<div className="kt-grid kt-grid--ver kt-grid--root">
				<div id="kt_login" className="kt-grid kt-grid--hor kt-grid--root  kt-login kt-login--v1">
					<div className="kt-grid__item kt-grid__item--fluid kt-grid kt-grid--desktop kt-grid--ver-desktop kt-grid--hor-tablet-and-mobile">
						<div
							className="kt-grid__item kt-grid__item--order-tablet-and-mobile-2 kt-grid kt-grid--hor kt-login__aside"
							style={{
								backgroundImage: `url('https://images.unsplash.com/photo-1518908336710-4e1cf821d3d1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1473&q=80')`,
								backgroundPositionX: '100%',
							}}
						>
							<div className="kt-grid__item">
								<Link to="/" className="kt-login__logo">
									<img alt="Logo" src={logo} style={{ height: '100px', width: '100px' }} />
								</Link>
							</div>
							<div className="kt-grid__item kt-grid__item--fluid kt-grid kt-grid--ver">
								<div className="kt-grid__item kt-grid__item--middle">
									<h3 className="kt-login__title">Welcome to Online Exam Platform!</h3>
									<h4 className="kt-login__subtitle">The ultimate Online Ielts Test platform</h4>
								</div>
							</div>
							<div className="kt-grid__item">
								<div className="kt-login__info">
									<div className="kt-login__copyright">&copy; 2020 Online Test</div>
									<div className="kt-login__menu">
										<Link to="/terms" className="kt-link">
											Privacy
										</Link>
										<Link to="/terms" className="kt-link">
											Legal
										</Link>
										<Link to="/terms" className="kt-link">
											Contact
										</Link>
									</div>
								</div>
							</div>
						</div>

						<div className="kt-grid__item kt-grid__item--fluid  kt-grid__item--order-tablet-and-mobile-1  kt-login__wrapper">
							<Switch>
								<Redirect from="/auth" exact={true} to="/auth/login" />

								<Route
									path="/auth/login"
									render={() => (
										<LoginForm dispatch={dispatch} loginRes={loginRes} handleLogin={handleLogin} />
									)}
								/>
								<Route
									path="/auth/registration"
									render={() => (
										<RegistrationForm
											dispatch={dispatch}
											registerRes={registerRes}
											handleRegister={handleRegister}
										/>
									)}
								/>
								<Route
									path="/auth/forgot-password"
									render={() => (
										<ForgotPasswordForm
											dispatch={dispatch}
											handleForgotPasswordPromise={props.handleForgotPasswordPromise}
										/>
									)}
								/>
								<Route
									path="/auth/reset-password/:token"
									render={() => (
										<ResetPasswordForm
											dispatch={dispatch}
											handleResetPasswordPromise={props.handleResetPasswordPromise}
										></ResetPasswordForm>
									)}
								></Route>
								<Route
									path="/auth/verify-email/:token"
									render={() => (
										<VerifyEmailForm
											dispatch={dispatch}
											handleVerifyEmailPromise={props.handleVerifyEmailPromise}
										></VerifyEmailForm>
									)}
								></Route>
							</Switch>
						</div>
					</div>
				</div>
			</div>
		);
	}

	return (
		<div className="kt-grid kt-grid--ver kt-grid--root">
			<div id="kt_login" className="kt-grid kt-grid--hor kt-grid--root  kt-login kt-login--v1">
				<div className="kt-grid__item kt-grid__item--fluid kt-grid kt-grid--desktop kt-grid--ver-desktop kt-grid--hor-tablet-and-mobile">
					<div className="kt-grid__item kt-grid__item--fluid  kt-grid__item--order-tablet-and-mobile-1  kt-login__wrapper">
						{formType === 'login' ? (
							<LoginForm
								dispatch={dispatch}
								loginRes={loginRes}
								handleChangeFormType={handleChangeFormType}
								handleLogin={handleLogin}
							/>
						) : null}
						{formType === 'register' ? (
							<RegistrationForm
								dispatch={dispatch}
								registerRes={registerRes}
								handleChangeFormType={handleChangeFormType}
								handleRegister={handleRegister}
							/>
						) : null}
						{formType === 'forgotPassword' ? (
							<ForgotPasswordForm
								handleChangeFormType={handleChangeFormType}
								dispatch={dispatch}
								handleForgotPasswordPromise={props.handleForgotPasswordPromise}
							/>
						) : null}
					</div>
				</div>
			</div>
		</div>
	);
}
