import React from 'react';
import styled from 'styled-components';
import { Typography, Card } from '@material-ui/core';
import { Rating } from '@material-ui/lab';
import { roundNumberToTwoDecimals } from 'utils/utils';
import { useHistory } from 'react-router-dom';
import Theme from 'app/themes/styles';
import PhotoDialog from '../PhotoDialog'
const Image = styled.img`
	width: 100%;
	height: auto;
	max-height: 250px;
	max-width: 250px;
	border-radius: 5px;

`;

const Title = styled(Typography)`
	margin-top: 25px !important;
	font-weight: 600 !important;
	text-align: center;

	&:hover {
		cursor: pointer;
		color: ${Theme.ncpTitleHover};
	}
`;

const Wrapper = styled(Card)`
	display: flex;
	align-items: center;
	justify-content: space-between;

	&:hover {
		cursor: pointer;
	}
`;

const VoteAmount = styled.em`
	position: relative;
	color: #536b83;
	font-size: 12px;

	padding-left: 5px;
	top: -4px;
	line-height: 1.5;
`;

const RatingText = styled.span`
	font-size: 16px;
	font-weight: 600;
	position: relative;
	color: #f9a95a;
	margin: 0px 4px 0px 0px;
	top: -4px;
`;

const InfoWrapper = styled.span`

`

const RatingWrapper = styled.div`
	display: flex;
	flex-direction: column;
`;

function TestCardItem(props) {
	const { title, rating, image, content } = props;
	const [photoDialogOpen, setPhotoDialogOpen] = React.useState(false)

	const handleOpenDialog = () => {
		setPhotoDialogOpen(true)
	}

	const handleCloseDialog = () => {
		console.log('here')
		setPhotoDialogOpen(false)
	}

	return (
		<Wrapper onClick={() => handleOpenDialog()} elevation={2}>
			<Image
				src="https://ieltsonlinetests.com/sites/default/files/styles/latest_collection/public/2020-01/MTjul2020%403x.png"
				alt="Test Image"
			></Image>
			<InfoWrapper>			
			<Title variant="h6" component="p">
				{title}
			</Title>
			<RatingWrapper>
				<div>
					<RatingText>{roundNumberToTwoDecimals(rating.starAmount)}</RatingText>
					<Rating
						name="simple-controlled"
						precision={0.5}
						value={roundNumberToTwoDecimals(rating.starAmount)}
						disabled
					/>
				</div>
				<div>
					<VoteAmount>({rating.voteAmount} votes)</VoteAmount>
				</div>
			</RatingWrapper>
			</InfoWrapper>
<PhotoDialog open={photoDialogOpen} title={title} image={image} content={content} handleOpen={handleOpenDialog} handleClose={handleCloseDialog} ></PhotoDialog>
		</Wrapper>
	);
}

export default TestCardItem;
