import React from 'react';
import styled from 'styled-components';
import { Typography } from '@material-ui/core';
import { Rating } from '@material-ui/lab';
import { roundNumberToTwoDecimals } from 'utils/utils';
import { useHistory } from 'react-router-dom';
import Theme from 'app/themes';

const Image = styled.img`
	width: 100%;
	height: auto;
	max-height: 200px;
	max-width: 200px;
`;

const Title = styled(Typography)`
	margin-top: 25px !important;
	font-weight: 600 !important;
	text-align: center;

	&:hover {
		cursor: pointer;
		color: ${Theme.ncpTitleHover};
	}
`;

const Wrapper = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;

	&:hover {
		cursor: pointer;
	}
`;

const VoteAmount = styled.em`
	position: relative;
	color: #536b83;
	font-size: 12px;

	padding-left: 5px;
	top: -4px;
	line-height: 1.5;
`;

const RatingText = styled.span`
	font-size: 16px;
	font-weight: 600;
	position: relative;
	color: #f9a95a;
	margin: 0px 4px 0px 0px;
	top: -4px;
`;

const RatingWrapper = styled.div`
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
`;

function TestCardItem(props) {
	const { title, rating, testId } = props;
	const history = useHistory();
	const handleClick = () => {
		history.push(`/test/${testId}`);
	};

	return (
		<Wrapper onClick={() => handleClick()}>
			<Image
				src="https://ieltsonlinetests.com/sites/default/files/styles/latest_collection/public/2020-01/MTjul2020%403x.png"
				alt="Test Image"
			></Image>
			<Title variant="h6" component="p">
				{title}
			</Title>
			<RatingWrapper>
				<div>
					<RatingText>{roundNumberToTwoDecimals(rating.starAmount)}</RatingText>
					<Rating
						name="simple-controlled"
						precision={0.5}
						value={roundNumberToTwoDecimals(rating.starAmount)}
						disabled
					/>
				</div>
				<div>
					<VoteAmount>({rating.voteAmount} votes)</VoteAmount>
				</div>{' '}
			</RatingWrapper>
		</Wrapper>
	);
}

export default TestCardItem;
